echo -e "  ${p}04m 04# ${ep}12m 12# ${ep}020m 020# ${ep}026m 026# ${ep}032m 032# ${ep}038m 038# ${ep}044m 044# ${ep}050m 050# ${ep}236m 236# ${e}|"
