echo -e "  ${p}06m 06# ${ep}14m 14# ${ep}052m 052# ${ep}058m 058# ${ep}064m 064# ${ep}070m 070# ${ep}076m 076# ${ep}082m 082# ${ep}238m 238# ${e}|"
echo -e "  ${p}07m 07# ${ep}15m 15# ${ep}053m 053# ${ep}059m 059# ${ep}065m 065# ${ep}071m 071# ${ep}077m 077# ${ep}083m 083# ${ep}239m 239# ${e}|"
echo -e "  ${p}07m 07# ${ep}15m 15# ${ep}054m 054# ${ep}060m 060# ${ep}066m 066# ${ep}072m 072# ${ep}078m 078# ${ep}084m 084# ${ep}240m 240# ${e}|"
