echo -e "  ${p}07m     ${ep}15m     ${ep}057m 057# ${ep}063m 063# ${ep}069m 069# ${ep}075m 075# ${ep}081m 081# ${ep}087m 087# ${ep}243m 243# ${e}|"
echo -e "  ${p}06m     ${ep}14m     ${ep}088m 088# ${ep}094m 094# ${ep}100m 100# ${ep}106m 106# ${ep}112m 112# ${ep}118m 118# ${ep}244m 244# ${e}|"
echo -e "  ${p}07m     ${ep}15m     ${ep}089m 089# ${ep}095m 095# ${ep}101m 101# ${ep}107m 107# ${ep}113m 113# ${ep}119m 119# ${ep}245m 245# ${e}|"
