#!/bin/bash

# 3/4 bit version of ANSI-color
# |------------+----------+------------------+------------------------------+--------------------------------------|
# | color-mode | octal    | description      | example (= in octal)         | NOTE                                 |
# |------------+----------+------------------+------------------------------+--------------------------------------|
# |          0 | \033[0m  | reset any affect | echo -e "\033[0m"            | 0m equals to m                       |
# |          1 | \033[1m  | light (= bright) | echo -e "\033[1m####\033[m"  | -                                    |
# |          2 | \033[2m  | dark (= fade)    | echo -e "\033[2m####\033[m"  | -                                    |
# |------------+----------+------------------+------------------------------+--------------------------------------|
# |  text-mode | octal    | description      | example (= in octal)         | NOTE                                 |
# |------------+----------+------------------+------------------------------+--------------------------------------|
# |          3 | \033[3m  | italic           | echo -e "\033[3m####\033[m"  |                                      |
# |          4 | \033[4m  | underline        | echo -e "\033[4m####\033[m"  |                                      |
# |          5 | \033[5m  | blink (slow)     | echo -e "\033[3m####\033[m"  |                                      |
# |          7 | \003[7m  | reverse          | echo -e "\033[7m####\033[m"  | it affects the background/foreground |
# |          8 | \033[8m  | hide             | echo -e "\033[8m####\033[m"  | it affects the background/foreground |
# |          9 | \033[9m  | cross            | echo -e "\033[9m####\033[m"  |                                      |
# |------------+----------+------------------+------------------------------+--------------------------------------|
# | foreground | octal    | description      | example (= in octal)         | NOTE                                 |
# |------------+----------+------------------+------------------------------+--------------------------------------|
# |         30 | \033[30m | black            | echo -e "\033[30m####\033[m" |                                      |
# |         31 | \033[31m | red              | echo -e "\033[31m####\033[m" |                                      |
# |         32 | \033[32m | green            | echo -e "\033[32m####\033[m" |                                      |
# |         33 | \033[32m | yellow           | echo -e "\033[33m####\033[m" |                                      |
# |         34 | \033[32m | blue             | echo -e "\033[34m####\033[m" |                                      |
# |         35 | \033[32m | purple           | echo -e "\033[35m####\033[m" | real name: magenta = reddish-purple  |
# |         36 | \033[32m | cyan             | echo -e "\033[36m####\033[m" |                                      |
# |         37 | \033[32m | white            | echo -e "\033[37m####\033[m" |                                      |
# |------------+----------+------------------+------------------------------+--------------------------------------|
# |         38 | 8/24        This is for special use of 8-bit or 24-bit                                            |
# |------------+----------+------------------+------------------------------+--------------------------------------|
# | background | octal    | description      | example (= in octal)         | NOTE                                 |
# |------------+----------+------------------+------------------------------+--------------------------------------|
# |         40 | \033[40m | black            | echo -e "\033[40m####\033[m" |                                      |
# |         41 | \033[41m | red              | echo -e "\033[41m####\033[m" |                                      |
# |         42 | \033[42m | green            | echo -e "\033[42m####\033[m" |                                      |
# |         43 | \033[43m | yellow           | echo -e "\033[43m####\033[m" |                                      |
# |         44 | \033[44m | blue             | echo -e "\033[44m####\033[m" |                                      |
# |         45 | \033[45m | purple           | echo -e "\033[45m####\033[m" | real name: magenta = reddish-purple  |
# |         46 | \033[46m | cyan             | echo -e "\033[46m####\033[m" |                                      |
# |         47 | \033[47m | white            | echo -e "\033[47m####\033[m" |                                      |
# |------------+----------+------------------+------------------------------+--------------------------------------|
# |         48 | 8/24        This is for special use of 8-bit or 24-bit                                            |                                                                                       |
# |------------+----------+------------------+------------------------------+--------------------------------------|

echo -e "\n3/4 bit version of ANSI-color"
