echo -e "  \033[04m 04 \033[m underline |  \033[24m 24 \033[m  |  \033[34m 34 \033[m  |  \033[44m 44 \033[m  |  \033[54m 54 \033[m  |  \033[64m 64 \033[m  |  \033[74m 74 \033[m  |  \033[84m 84 \033[m  |  \033[94m 94 \033[m  |  \033[104m 104 \033[m  |  \033[114m 114 \033[m  |  \033[124m 124 \033[m"
echo -e "  \033[05m 05 \033[m           |  \033[25m 25 \033[m  |  \033[35m 35 \033[m  |  \033[45m 45 \033[m  |  \033[55m 55 \033[m  |  \033[65m 65 \033[m  |  \033[75m 75 \033[m  |  \033[85m 85 \033[m  |  \033[95m 95 \033[m  |  \033[105m 105 \033[m  |  \033[115m 115 \033[m  |  \033[125m 125 \033[m"
echo -e "  \033[06m 06 \033[m           |  \033[26m 26 \033[m  |  \033[36m 36 \033[m  |  \033[46m 46 \033[m  |  \033[56m 56 \033[m  |  \033[66m 66 \033[m  |  \033[76m 76 \033[m  |  \033[86m 86 \033[m  |  \033[96m 96 \033[m  |  \033[106m 106 \033[m  |  \033[116m 116 \033[m  |  \033[126m 126 \033[m"
echo -e "  \033[07m 07 \033[m reverse   |  \033[27m 27 \033[m  |  \033[37m 37 \033[m  |  \033[47m 47 \033[m  |  \033[57m 57 \033[m  |  \033[67m 67 \033[m  |  \033[77m 77 \033[m  |  \033[87m 87 \033[m  |  \033[97m 97 \033[m  |  \033[107m 107 \033[m  |  \033[117m 117 \033[m  |  \033[127m 127 \033[m"
echo -e "  \033[08m 08 \033[m hide      |  \033[28m 28 \033[m  |  \033[38m 38 \033[m  |  \033[48m 48 \033[m  |  \033[58m 58 \033[m  |  \033[68m 68 \033[m  |  \033[78m 78 \033[m  |  \033[88m 88 \033[m  |  \033[98m 98 \033[m  |  \033[108m 108 \033[m  |  \033[118m 118 \033[m  |  \033[128m 128 \033[m"
echo -e "  \033[09m 09 \033[m cross     |  \033[29m 29 \033[m  |  \033[39m 39 \033[m  |  \033[49m 49 \033[m  |  \033[59m 59 \033[m  |  \033[69m 69 \033[m  |  \033[79m 79 \033[m  |  \033[89m 89 \033[m  |  \033[99m 99 \033[m  |  \033[109m 109 \033[m  |  \033[119m 119 \033[m  |  \033[129m 129 \033[m"
echo ""


